function largestOfFour(arr) {
  const largestArr = []

  for (let i = 0; i < arr.length; i++) {
    let largest = arr[i][0]
    for (let j = 0; j < arr[i].length; j++) {
      if (arr[i][j] >= largest) {
        largest = arr[i][j]
      }
    }
    largestArr.push(largest)
  }

  return largestArr
}

