function chunkArrayInGroups(arr, size) {
  const matrix = []

  let j = 0
  let inner = []

  for (let i = 0; i < arr.length; i++) {
    if (j === size) {
      j = 0
      matrix.push(inner)
      inner = []
    }

    inner.push(arr[i])

    j++
  }

  if (inner.length > 0) {
    matrix.push(inner)
  }

  console.log(matrix)
  return matrix
}

chunkArrayInGroups(["a", "b", "c", "d"], 2) // [["a", "b"], ["c", "d"]].
