function binaryAgent(str) {
  const arr = str.split(' ')

  // convert binary to decimal
  for (let i in arr) {
    arr[i] = parseInt(arr[i], 2)
  }

  // convert decimal to word
  for (let i in arr) {
    arr[i] = String.fromCharCode(arr[i])
  }

  console.log(arr.join(' '))
  return arr.join('')
}

