function orbitalPeriod(arr) {
  const GM = 398600.4418
  const earthRadius = 6367.4447
  const pi = 2 * Math.PI

  for (let i in arr) {
    const c = earthRadius + arr[i]['avgAlt']
    const c3 = Math.pow(c, 3)
    const root = Math.sqrt(c3 / GM)
    const res = Math.round(pi * root)

    delete arr[i]['avgAlt']
    arr[i]['orbitalPeriod'] = res
  }

  return arr
}

