function titleCase(str) {
  let arr = str.split(' ')

  for (let i = 0; i < arr.length; i++) {
    let word = arr[i].split('')
    word[0] = word[0].toUpperCase()
    for (let j = 1; j < arr[i].length; j++) {
      word[j] = word[j].toLowerCase()
    }
    arr[i] = word.join('')
  }

  arr = arr.join(' ')

  return arr;
}

