function frankenSplice(arr1, arr2, n) {
  // make a copy of arr2
  const arr = arr2.slice(0, arr2.length)

  // insert all elements of arr1 into arr using spread operator
  arr.splice(n, 0, ...arr1)

  return arr
}

frankenSplice([1, 2, 3], [4, 5, 6], 1) // [4, 1, 2, 3, 5, 6]
frankenSplice([1, 2, 3], [4, 5], 1) // [4, 1, 2, 3, 5]
frankenSplice([1, 2], ["a", "b"], 1) // ["a", 1, 2, "b"]
frankenSplice(["claw", "tentacle"], ["head", "shoulders", "knees", "toes"], 2) // ["head", "shoulders", "claw", "tentacle", "knees", "toes"]
