function pairElement(str) {
  const dna = []

  for (const char in str.split('')) {
    const pairing = []
    pairing.push(str[char].toUpperCase())

    switch (str[char].toUpperCase()) {
      case 'A':
        pairing.push('T')
        break;
      case 'T':
        pairing.push('A')
        break;
      case 'C':
        pairing.push('G')
        break;
      case 'G':
        pairing.push('C')
        break;
    }

    dna.push(pairing)
  }

  console.log(dna)
  return dna
}

