function smallestCommons(arr) {
  let min = Math.min(arr[0], arr[1])
  const max = Math.max(arr[0], arr[1])
  const range = []

  while (min <= max) {
    range.push(min++)
  }
  range.sort()

  let quot = 0
  let loop = 1
  let n

  while (n !== range.length) {
    quot = range[0] * loop * range[1]

    for (n = 2; n < range.length; n++) {
      if (quot % range[n] !== 0) {
        break
      }
    }

    loop++
  }

  console.log(quot)
  return quot
}


