function steamrollArray(arr) {
  // I'm a steamroller, baby
  const flatArr = []

  const flatten = arg => {
    if (!Array.isArray(arg)) {
      flatArr.push(arg)
    } else {
      for (let j in arg) {
        flatten(arg[j])
      }
    }
  }

  for (let i in arr) {
    flatten(arr[i])
  }

  console.log(flatArr)
  return flatArr
}

